package com.longfe;import ognl.Ognl;public class TestNgnl {	public static void main(String[] args) throws Exception {		Object obj = Ognl.parseExpression("name != null and name != ''");		Person person = new Person();		person.setName("");		Object obj2 = Ognl.getValue(obj, person);		System.out.println(obj2);	}}class Person {	String name;	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}}