package com.longfe.parse.jdbc;import com.alibaba.druid.pool.DruidDataSource;import com.longfe.parse.sql.XMLScriptSqlBuilder;import org.apache.ibatis.io.Resources;import org.w3c.dom.Document;import org.w3c.dom.Element;import org.w3c.dom.NamedNodeMap;import org.w3c.dom.Node;import org.xml.sax.SAXException;import javax.xml.parsers.DocumentBuilder;import javax.xml.parsers.DocumentBuilderFactory;import javax.xml.parsers.ParserConfigurationException;import java.io.IOException;public class XMLScriptJdbcBuidler {	public void readXML() throws Exception {		DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();		try {			DocumentBuilder builder = factory.newDocumentBuilder();			Document doc = builder.parse(XMLScriptSqlBuilder.class.getResourceAsStream("jdbc.xml"));			Element element = doc.getDocumentElement();			buildDataBase(element);		} catch (ParserConfigurationException e) {			e.printStackTrace();		} catch (SAXException e) {			e.printStackTrace();		} catch (IOException e) {			e.printStackTrace();		}	}	public void buildDataBase(Node node) throws Exception {		String nodeName = node.getNodeName();		NamedNodeMap nnmap = node.getAttributes();		if ("database".equals(nodeName)) {			String type = nnmap.getNamedItem("type").getNodeValue();			DruidDataSource database = (DruidDataSource) Resources.classForName(type).newInstance();		}	}}